CREATE TABLE `cms_page` (
  `page_id` smallint NOT NULL COMMENT 'Entity Id',
  `title` varchar(255) DEFAULT NULL COMMENT 'Page Title',
  `page_layout` varchar(255) DEFAULT NULL COMMENT 'Page Layout',
  `meta_keywords` text COMMENT 'Page Meta Keywords',
  `meta_description` text COMMENT 'Page Meta Description',
  `identifier` varchar(100) DEFAULT NULL COMMENT 'Page String Identifier',
  `content_heading` varchar(255) DEFAULT NULL COMMENT 'Page Content Heading',
  `content` mediumtext COMMENT 'Page Content',
  `creation_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT 'Page Creation Time',
  `update_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT 'Page Modification Time',
  `is_active` smallint NOT NULL DEFAULT '1' COMMENT 'Is Page Active',
  `sort_order` smallint NOT NULL DEFAULT '0' COMMENT 'Page Sort Order',
  `layout_update_xml` text COMMENT 'Page Layout Update Content',
  `custom_theme` varchar(100) DEFAULT NULL COMMENT 'Page Custom Theme',
  `custom_root_template` varchar(255) DEFAULT NULL COMMENT 'Page Custom Template',
  `custom_layout_update_xml` text COMMENT 'Page Custom Layout Update Content',
  `layout_update_selected` varchar(128) DEFAULT NULL COMMENT 'Page Custom Layout File',
  `custom_theme_from` date DEFAULT NULL COMMENT 'Page Custom Theme Active From Date',
  `custom_theme_to` date DEFAULT NULL COMMENT 'Page Custom Theme Active To Date',
  `meta_title` varchar(255) DEFAULT NULL COMMENT 'Page Meta Title',
  `website_root` smallint unsigned NOT NULL DEFAULT '1' COMMENT 'Website Root',
  `row_id` smallint NOT NULL AUTO_INCREMENT COMMENT 'Version Id',
  `created_in` bigint unsigned NOT NULL DEFAULT '1' COMMENT 'Update Id',
  `updated_in` bigint unsigned NOT NULL DEFAULT '2147483647' COMMENT 'Next Update Id',
  `top_banner_background_image` varchar(255) DEFAULT NULL COMMENT 'Top Banner Background Image',
  `cover_image` varchar(255) DEFAULT NULL COMMENT 'Cover Image',
  `cover_background_image` varchar(255) DEFAULT NULL COMMENT 'Cover Background Image',
  `cover_background_color` varchar(255) DEFAULT NULL COMMENT 'Cover Background Color',
  `subtitle` text COMMENT 'Subtitle',
  `short_description` text COMMENT 'Short Description',
  `is_include_search` smallint unsigned NOT NULL DEFAULT '0' COMMENT 'Is Include Search',
  `is_excluded_from_search_result` smallint unsigned NOT NULL DEFAULT '0' COMMENT 'Is Excluded From Search Search',
  `content_text` mediumtext COMMENT 'Page content text without html tags',
  `page_header_render` varchar(50) DEFAULT NULL COMMENT 'Page Header Render',
  `news_date` date DEFAULT NULL COMMENT 'News Date',
  `news_type` int unsigned NOT NULL DEFAULT '0' COMMENT 'News Type ID',
  `display_mode` smallint unsigned NOT NULL DEFAULT '0' COMMENT 'Display Mode',
  `news_sort_direction` varchar(8) NOT NULL DEFAULT 'desc' COMMENT 'News Sort Direction',
  `news_redirect_url` varchar(255) DEFAULT NULL COMMENT 'News Redirect Url',
  `grid_title` text COMMENT 'Grid Title',
  `grid_description` text COMMENT 'Grid Description',
  PRIMARY KEY (`row_id`),
  KEY `CMS_PAGE_IDENTIFIER` (`identifier`),
  KEY `CMS_PAGE_PAGE_ID` (`page_id`),
  KEY `CMS_PAGE_CREATED_IN` (`created_in`),
  KEY `CMS_PAGE_UPDATED_IN` (`updated_in`),
  FULLTEXT KEY `CMS_PAGE_TITLE_META_KEYWORDS_META_DESCRIPTION_IDENTIFIER_CONTENT` (`title`,`meta_keywords`,`meta_description`,`identifier`,`content`),
  FULLTEXT KEY `CMS_PAGE_TITLE_IDENTIFIER_CONTENT_SUBTITLE_SHORT_DESCRIPTION` (`title`,`identifier`,`content`,`subtitle`,`short_description`),
  FULLTEXT KEY `CMS_PAGE_TITLE_IDENTIFIER_CONTENT_TEXT` (`title`,`identifier`,`content_text`)
) ENGINE=InnoDB AUTO_INCREMENT=336 DEFAULT CHARSET=utf8mb3 COMMENT='CMS Page Table';
